function sortearCarta(){
  var numeroCartaMaquina = (Math.floor(Math.random() * (7 - 0 + 1)) + 0);
  cartaMaquina = cartas[numeroCartaMaquina];
  // console.log(cartaMaquina);
  
  var numeroCartaJogador = (Math.floor(Math.random() * (7 - 0 + 1)) + 0);
  while(numeroCartaJogador == numeroCartaMaquina){
      numeroCartaJogador = (Math.floor(Math.random() * (7 - 0 + 1)) + 0);
  }
  cartaJogador = cartas[numeroCartaJogador];
  
  document.getElementById("btnSortear").disabled = true;
  document.getElementById("btnJogar").disabled = false;
  
  var cartaOponente = "";
  cartaOponente += '<img class="bandeiraNome" src="https://i.pinimg.com/originals/45/94/95/4594958dca5def8d1415c40fe653a698.png" alt="">';
  cartaOponente += "<h4>" + cartaMaquina.nome +"</h4>";
  cartaOponente += '<img class="moldura" src="https://4.bp.blogspot.com/-FB2EQ2vCQDo/U30r1HwUDmI/AAAAAAAANgQ/OoPdMHrPZsA/s200/Moldura++quadrada+madeira+prego.png" alt="">';
  cartaOponente += "<p class='alinharFoto'>" + cartaMaquina.foto +"</p>";
  cartaOponente += '<img class="divisor" src="https://static.wikia.nocookie.net/tudosobrehoradeaventura/images/5/51/Seta-espada.png/revision/latest?cb=20130724170451&path-prefix=pt-br" alt=""><div class="atributos">';
  cartaOponente += '<img id="vs" src="https://cdn-0.imagensemoldes.com.br/wp-content/uploads/2021/03/Foto-VS-PNG-1024x862.png" alt="">';
  cartaOponente += "<div class='oponente' id='oponente'><p><b> Poderes:</b> " + cartaMaquina.atributos.poderes + "</p><div class='barra'><div class='progresso' style='width:" + cartaMaquina.atributos.poderes + "%'></div></div>";
  cartaOponente += "<p><b> Inteligência:</b> " + cartaMaquina.atributos.inteligencia + "</p><div class='barra'><div class='progresso' style='width:" + cartaMaquina.atributos.inteligencia + "%'></div></div>";
  cartaOponente +="<p><b> Força:</b> " + cartaMaquina.atributos.forca + "</p><div class='barra'><div class='progresso' style='width:" + cartaMaquina.atributos.forca + "%'></div></div>";
  cartaOponente +="<p><b> Espirito aventureiro:</b> " + cartaMaquina.atributos.espiritoAventureiro + "</p><div class='barra'><div class='progresso' style='width:" + cartaMaquina.atributos.espiritoAventureiro + "%'></div></div>";
  cartaOponente +="<p><b> Amizade:</b> " + cartaMaquina.atributos.amizade + "</p><div class='barra'><div class='progresso' style='width:" + cartaMaquina.atributos.amizade + "%'></div></div></div>";
  
  document.getElementById("computador").innerHTML = cartaOponente;
  
  document.getElementById("player").style.visibility = "visible";
  document.getElementById("computador").style.visibility = "visible";
  
  exibeCartaJogador();
}

function exibeCartaJogador() {
  
    var suaCarta = "";
    suaCarta += '<img class="bandeiraNome" src="https://i.pinimg.com/originals/45/94/95/4594958dca5def8d1415c40fe653a698.png" alt="">';
    suaCarta += "<h4>" + cartaJogador.nome +"</h4>";
    suaCarta += '<img class="moldura" src="https://4.bp.blogspot.com/-FB2EQ2vCQDo/U30r1HwUDmI/AAAAAAAANgQ/OoPdMHrPZsA/s200/Moldura++quadrada+madeira+prego.png" alt="">';
    suaCarta += "<p class='alinharFoto'>" + cartaJogador.foto +"</p>";
    suaCarta += '<img class="divisor" src="https://static.wikia.nocookie.net/tudosobrehoradeaventura/images/5/51/Seta-espada.png/revision/latest?cb=20130724170451&path-prefix=pt-br" alt="">';
    // <input type='radio' name='atributo' value='" + atributo + "'>" + atributo + "</div>";
    suaCarta += "<div class='atributos'><input type='radio' name='atributo' id='poderes' value='poderes'><label for='poderes'><p><b> Poderes:</b> " + cartaJogador.atributos.poderes + "</p></label><div class='barra'><div class='progresso' style='width:" + cartaJogador.atributos.poderes + "%'></div></div>";
    suaCarta += "<input type='radio' name='atributo' id='inteligencia' value='inteligencia'><label for='inteligencia'><p><b> Inteligência:</b> " + cartaJogador.atributos.inteligencia + "</p></label><div class='barra'><div class='progresso' style='width:" + cartaJogador.atributos.inteligencia + "%'></div></div>";
    suaCarta +="<input type='radio' name='atributo' id='forca' value='forca'><label for='forca'><p><b> Força:</b> " + cartaJogador.atributos.forca + "</p></label><div class='barra'><div class='progresso' style='width:" + cartaJogador.atributos.forca + "%'></div></div>";
    suaCarta +="<input type='radio' name='atributo' id='espiritoAventureiro' value='espiritoAventureiro'><label for='espiritoAventureiro'><p><b> Espirito aventureiro:</b> " + cartaJogador.atributos.espiritoAventureiro + "</p></label><div class='barra'><div class='progresso' style='width:" + cartaJogador.atributos.espiritoAventureiro + "%'></div></div>";
    suaCarta +="<input type='radio' name='atributo' id='amizade' value='amizade'><label for='amizade'><p><b> Amizade:</b> " + cartaJogador.atributos.amizade + "</p></label><div class='barra'><div class='progresso' style='width:" + cartaJogador.atributos.amizade + "%'></div></div></div>";

    document.getElementById("player").innerHTML = suaCarta;
}

function obtemAtributoSelecionado(){
  var radioAtributo = document.getElementsByName("atributo");
  for(var i = 0; i < radioAtributo.length; i++){
    if(radioAtributo[i].checked){
      return radioAtributo[i].value;
    }
  }
}

function jogar() {
  var atributoSelecionado = obtemAtributoSelecionado();
  
  if(atributoSelecionado != null){
    
    document.getElementById("btnSortear").disabled = false;
    document.getElementById("btnJogar").disabled = true;
    
    document.getElementById("oponente").style.visibility = "visible";
    document.getElementById("vs").style.visibility = "hidden";
    
    if(cartaJogador == cartas[2]){
      Swal.fire({
        icon: 'success',
        title: 'Parabéns',
        text: 'Você venceu!'
      })
    }
    else if(cartaMaquina == cartas[2]){
      Swal.fire({
        icon: 'error',
        title: 'Não foi dessa vez',
        text: 'Você perdeu.'
      })
    }
    else if(cartaJogador.atributos[atributoSelecionado] > cartaMaquina.atributos[atributoSelecionado]) {
      // alert("Parabéns, você venceu!");
      Swal.fire({
        icon: 'success',
        title: 'Parabéns',
        text: 'Você venceu!'
      })
    } else if(cartaJogador.atributos[atributoSelecionado] < cartaMaquina.atributos[atributoSelecionado]){
      // alert("Não foi dessa vez");
      Swal.fire({
        icon: 'error',
        title: 'Não foi dessa vez',
        text: 'Você perdeu.'
      })
    } else {
      // alert("Vocês empataram");
      Swal.fire({
        icon: 'info',
        title: 'Vocês empataram',
        text: 'Tente novamente.'
      })
    }
  }
}
